package corejava;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=0;
				//initializing the condition saying x=0
	while (x<20)//-condition if it is true,it will display and also increase the value by 1
	{
		System.out.println(x);
		x+=2;//you can also do x++ for x+1 only but for other it has to be number specified like x+=2
		
		
		
	}
		
		
		
	}

}
